mapper.select:
select

from orders
where external_order_no = #{externalOrderNo, jdbcType=VARCHAR}

SELECT o.id, u.username, o.total_amount, p.provider_order_no, CASE WHEN o.paid = 1 THEN N'已付款' ELSE N'未付款' END AS payment_status, o.status, s.name AS shipping_method_name, o.invoice_type, CASE WHEN i.invoice_number IS NOT NULL THEN N'已開立' ELSE N'未開立' END AS invoice_status, CASE WHEN t.create_status IS NOT NULL THEN N'已成立' ELSE N'未成立' END AS logistics_status , t.status logistics_record_status, ISNULL(t.waybill_no, t.vendororder) AS tracking_no, o.create_time FROM orders o LEFT JOIN payment_transaction p ON p.id = o.external_order_no LEFT JOIN invoice i ON i.external_order_no = o.external_order_no LEFT JOIN shipping_method s ON o.shipping_method_id = s.id LEFT JOIN users u ON u.id = o.user_id LEFT JOIN shipping_tracking t ON o.id = t.order_id
AND o.id = #{orderId}
AND u.username LIKE CONCAT('%', #{username}, '%')
AND o.status = #{status}
AND o.paid = 1
AND o.paid = 0
AND o.create_time >= #{startTime}
AND o.create_time <= #{endTime}
ORDER BY o.create_time DESC
